    cout &lt;&lt; "好基友正在访问" &lt;&lt; building-&gt;m_SittingRoom &lt;&lt; endl;
    cout &lt;&lt; "好基友正在访问" &lt;&lt; building-&gt;m_BedRoom &lt;&lt; endl;
}

void test01()
{
    goodGay gg;
    gg.visit();

}

int main(){

    test01();

    system("pause");
    return 0;
