C++<pre><code>#include &lt;iostream&gt;
using namespace std;

int main() {
    int a = 10;
    int* p = &amp;a;  // 定义指针并指向 a

    cout &lt;&lt; "a = " &lt;&lt; a &lt;&lt; endl;
    cout &lt;&lt; "*p = " &lt;&lt; *p &lt;&lt; endl;

    *p = 20;  // 修改指针指向的变量值
    cout &lt;&lt; "a after modification = " &lt;&lt; a &lt;&lt; endl;

    return 0;
}
</code></pre> <p>🟢 输出：</p> Text Only<pre><code>a = 10
*p = 10
a after modification = 20
</code></pre>

C++<pre><code>#include &lt;iostream&gt;
using namespace std;

int main() {
    int a = 10;
    int&amp; ref = a;  // 定义引用，必须立即初始化

    cout &lt;&lt; "a = " &lt;&lt; a &lt;&lt; endl;
    cout &lt;&lt; "ref = " &lt;&lt; ref &lt;&lt; endl;

    ref = 30;  // 修改引用所绑定的变量值
    cout &lt;&lt; "a after modification = " &lt;&lt; a &lt;&lt; endl;

    return 0;
}
</code></pre> <p>🟢 输出：</p> Text Only<pre><code>a = 10
ref = 10
a after modification = 30
</code></pre>

C++<pre><code>void modifyByPointer(int* p) {
    *p = 100;
}

void modifyByReference(int&amp; r) {
    r = 200;
}

int main() {
    int x = 10;
    modifyByPointer(&amp;x);   // 传地址
    cout &lt;&lt; "x after pointer: " &lt;&lt; x &lt;&lt; endl;

    modifyByReference(x);  // 传引用
    cout &lt;&lt; "x after reference: " &lt;&lt; x &lt;&lt; endl;

    return 0;
}
</code></pre> <p>🟢 输出：</p> Text Only<pre><code>x after pointer: 100
x after reference: 200
</code></pre>

