C++<pre><code>#include &lt;iostream&gt;
#include &lt;thread&gt;

void task() {
    std::cout &lt;&lt; "线程ID: " &lt;&lt; std::this_thread::get_id() &lt;&lt; "\n";
}

int main() {
    std::thread t(task);  // 创建线程
    t.join();             // 等待线程执行完成
    return 0;
