</code></pre> C++<pre><code>#include &lt;iostream&gt;
#include &lt;thread&gt;
using namespace std;

void thread_1()
{
    cout&lt;&lt;"子线程1"&lt;&lt;endl;
}

void thread_2(int x)
{
    cout&lt;&lt;"x:"&lt;&lt;x&lt;&lt;endl;
    cout&lt;&lt;"子线程2"&lt;&lt;endl;
}

int main()
{
  thread first ( thread_1); // 开启线程，调用：thread_1()
  thread second (thread_2,100); // 开启线程，调用：thread_2(100)
  //thread third(thread_2,3);//开启第3个线程，共享thread_2函数。
  std::cout &lt;&lt; "主线程\n";

  first.join(); //必须说明添加线程的方式            
  second.join(); 
  std::cout &lt;&lt; "子线程结束.\n";//必须join完成
  return 0;
}
</code></pre>

C++<pre><code>#include &lt;iostream&gt;
#include &lt;thread&gt;
#include &lt;chrono&gt;

void task() {
    std::cout &lt;&lt; "任务开始..." &lt;&lt; std::endl;
    std::this_thread::sleep_for(std::chrono::seconds(2)); // 睡 2 秒
    std::cout &lt;&lt; "任务结束！" &lt;&lt; std::endl;
}

int main() {
    std::thread t(task);
    t.join();
    return 0;
}
</code></pre>

C++<pre><code>#include &lt;iostream&gt;
